sp_angular_provider.script:
function peprForm ($timeout) {

	var template = '<sp-model form_model="peprSpForm" mandatory="mandatory"></sp-model>';

	function controller ($scope, $element) {
		$scope.$on('pepr.save.subform', function() {
			console.log(123);
			var glideForm = $element.find('div[form_model]').scope().$parent.$parent.getGlideForm();

			$timeout(function() {
				glideForm.submit('sysverb_update_and_stay');
				console.log('saved subform');
			});
		});
	}

	return {
		restrict: 'E',
		template: template,
		scope: {
			peprSpForm: '='
		},
		controller: ['$scope', '$element', controller]
	};
}
