sp_widget.client_script:
function(spUtil, $scope, $element, $timeout) {
  var c = this;
	c.forms = [];

	c.mainFormExpanded = true;



	c.newForm = function () {
		spUtil.get('pe-multi-form', {action: 'get_sub_form'}).then(function(response){
			c.forms.push(response.data.newForm);
		});
	}

	c.submit = function () {
		var mainGlideForm = $element.find('div[form_model] div').scope().$parent.$parent.getGlideForm();
		$timeout(function() {
			mainGlideForm.submit('sysverb_update_and_stay');
		});
		console.log('aaa');
		$scope.$broadcast('save_subform');
	}

	$scope.$on('pepr.save.subform', function () {
		console.log('bbb');
	});
}

sp_widget.css:
v-pane {
  border: 1px solid #ccc;
  border-radius: 5px;
  padding: 15px;
  margin: 10px 0;
}

sp_widget.script:
(function($sp) {

	if (input && input.action == 'get_sub_form') {
		data.newForm = getSubForm();
		return;
	}

	data.mainForm = $sp.getForm('x_snc_pepr_requisition', '-1');

	function getSubForm() {
		return $sp.getForm('x_snc_pepr_item', '-1');
	}

})($sp);
